// 1. Desencapsular JSON anidado
let raw = $json.output;

if (typeof raw === "string") {
  try {
    raw = JSON.parse(raw);
  } catch (e) {
    return { output: "‚ùå Error al parsear salida: " + e.message };
  }
}

// 2. Obtener HTML del agente
let html = raw.output || "";

// ELIMINAR LA SECCI√ìN DE RESPUESTA COMPLETA
// Esto elimina desde el <h3>Respuesta</h3> hasta antes de <div class="mv-evidence-box">
html = html.replace(
  /<h3 class=['"]mv-title-h3['"]>Respuesta<\/h3>[\s\S]*?(?=<div class=['"]mv-evidence-box['"]>)/i,
  ""
);


// 3. EXTRAER TODAS LAS FILAS DEL <table>
const tableMatch = html.match(/<table[\s\S]*?<\/table>/i);
if (!tableMatch) {
  return { output: html };
}

let tableHtml = tableMatch[0];

// Extraer filas individuales
const rows = [...tableHtml.matchAll(/<tr>([\s\S]*?)<\/tr>/gi)].map(r => r[0]);

// Crear diccionario para las filas encontradas
let map = {
  documento: null,
  ubicacion: null,
  pagina: null,
  enlace: null,
  seccion: null,
  descripcion: null
};

// Clasificar filas por su t√≠tulo
for (const row of rows) {
  const lower = row.toLowerCase();
  if (lower.includes("<td>documento")) map.documento = row;
  else if (lower.includes("<td>ubicaci√≥n")) map.ubicacion = row;
  else if (lower.includes("<td>p√°gina") || lower.includes("<td>pagina")) map.pagina = row;
  else if (lower.includes("<td>enlace")) map.enlace = row;
  else if (lower.includes("<td>secci√≥n") || lower.includes("<td>seccion")) map.seccion = row;
  else if (lower.includes("<td>descripci√≥n") || lower.includes("<td>descripcion")) map.descripcion = row;
}

// 4. RECONSTRUIR LA TABLA EN EL ORDEN CORRECTO
let newTable =
  "<table class=\"mv-evidence-table\">" +
  (map.documento || "") +
  (map.ubicacion || "") +
  (map.pagina || "") +
  (map.enlace || "") +
  (map.seccion || "") +
  (map.descripcion || "") +
  "</table>";

// 5. Reemplazar tabla original
html = html.replace(tableHtml, newTable);

// 6. Devolver HTML corregido
return {
  output: html
};

