    </div>
  `;
  document.body.appendChild(widget);

  const style = document.createElement("style");
  style.textContent = `
    #mv-widget { position:fixed; bottom:24px; right:24px; z-index:99999; font-family:'Segoe UI',Arial,sans-serif;}

    #mv-bot {
