    #mv-chatbox {
      position:fixed; bottom:110px; right:32px; width:380px; background:#fff;
      border-radius:22px;box-shadow:0 10px 50px rgba(0,0,0,0.15);
      flex-direction:column;display:none;overflow:hidden;
    }

    #mv-header { background:linear-gradient(90deg, #0037ff 0%, #063bce 100%); padding:12px 14px 0 14px; }
    #mv-header .mv-header-top { display:flex;align-items:center;gap:10px; padding:8px 6px 10px 6px; }
    #mv-header .mv-avatar { width:26px;height:26px;border-radius:50%; border:2px solid rgba(255,255,255,0.75); background:#fff; }
