    #mv-header-bar.active { background:#002B49; }

    #mv-messages {
      max-height:420px;min-height:140px;overflow-y:auto;padding:14px 12px;background:#fafafa;
      display:flex;flex-direction:column;gap:10px;
    }

    /* Caja evidencia */
    .mv-evidence-box {
      background:#f4f7ff;
      border:1.5px solid #0037ff33;
      border-radius:14px;
      padding:14px 16px;
      margin-top:8px;
      margin-bottom:12px;
      width: 100%;
      box-sizing: border-box;
      overflow-wrap: break-word;
    }

    .mv-title-h3 { font-size:16px; font-weight:700; color:#0037ff; margin-bottom:6px; margin-top:10px; }

    .mv-fragment {
      background:#ffffff;
      border-left:4px solid #063bce;
      padding:10px 14px;
      margin-top:10px;
      border-radius:10px;
      font-size:14px;
      line-height:1.45;
      color:#333;
      box-sizing: border-box;
    }

    .mv-evidence-table { 
      width:100%; 
      border-collapse:collapse; 
      margin-top:6px; 
      font-size:14px; 
      table-layout: fixed;
    }

    /* --- CAMBIO AQU√ç: Justificaci√≥n del texto --- */
    .mv-evidence-table td { 
      padding:6px 4px; 
      border-bottom:1px solid #e1e7ff; 
      word-wrap: break-word;
      overflow-wrap: break-word;
      text-align: justify; /* Justifica el texto del contenido */
      -webkit-hyphens: auto; /* Ayuda a cortar palabras largas elegantemente */
      hyphens: auto;
    }

    /* La primera columna (etiquetas) se mantiene a la izquierda */
    .mv-evidence-table td:first-child { 
      font-weight:600; 
      color:#063bce; 
      width: 35%; 
      text-align: left; 
    }

    /* Render HTML */
    .mv-message-text table { width: 100%; border-collapse: collapse; margin-top: 10px; }
    .mv-message-text td { padding: 6px 4px; }
    .mv-message-text ul { margin-left: 18px; margin-top: 10px; margin-bottom: 10px; }
