    #mv-messages::-webkit-scrollbar { width:8px; }
    #mv-messages::-webkit-scrollbar-thumb { background:#0037ff;border-radius:10px; }

    .mv-message-container { display:flex; width: 100%; }

    /* Alineaci√≥n */
    .mv-message-container.user { justify-content:flex-end; }
    .mv-message-container.bot { justify-content:flex-start; }

    /* Burbujas */
    .mv-message {
      border-radius:12px;
      padding:12px 14px;
      max-width: 85%;
