    #mv-footer { display:flex;align-items:center;gap:8px; padding:10px 12px 12px; background:#fff; }
    #mv-input { flex:1;padding:12px;border:1.5px solid #0037ff;border-radius:12px; font-size:15px; }
    #mv-send { width:44px;height:44px;border:none;border-radius:12px;background:#0037ff;color:#fff; cursor:pointer; }

    /* =================== PERSONALIZACIONES =================== */

    .mv-message-sender {
      font-weight: 700;
      margin-bottom: 6px;
      display: block;
    }

    /* Usuario: azul pastel */
    .mv-message-container.user .mv-message {
      background:#dbe7ff;
      border:1px solid #b4ccff;
      color:#003b8e;
      border-radius:16px 16px 4px 16px;
    }

    /* Agente: violeta pastel */
    .mv-message-container.bot .mv-message {
      background:#efe6ff;
      border:1px solid #d8c8ff;
      color:#4a2b8a;
      border-radius:16px 16px 16px 4px;
    }

