      font-size:11px !important;
      opacity:0.65;
      margin-top:8px;
      display:block;
      text-align:right;
    }
  `;
  document.head.appendChild(style);

  // Sanitizador
  function sanitizeWithLinks(html) {
    const allowedTags = ["a","p","strong","b","i","em","u","div","span","br","hr","h3","ul","ol","li","table","thead","tbody","tr","td","th"];
    const allowedAttrs = ["href","target","rel","class"];
    const div = document.createElement("div");
    div.innerHTML = html;
    const walker = document.createTreeWalker(div, NodeFilter.SHOW_ELEMENT, null);
    const toRemove = [];
    while (walker.nextNode()) {
      const el = walker.currentNode;
      if (!allowedTags.includes(el.tagName.toLowerCase())) { toRemove.push(el); continue; }
      [...el.attributes].forEach(attr => {
        if (!allowedAttrs.includes(attr.name.toLowerCase())) el.removeAttribute(attr.name);
      });
      if (el.tagName.toLowerCase() === "a") {
        el.setAttribute("target","_blank");
        el.setAttribute("rel","noopener noreferrer");
      }
    }
    toRemove.forEach(el => el.remove());
    return div.innerHTML;
  }

  function sanitize(text){
    const div=document.createElement("div");
    div.textContent=String(text ?? "");
    return div.innerHTML;
  }

  function appendMessage(sender, text, isUser=false){
    const container=document.createElement("div");
    container.className=`mv-message-container ${isUser?'user':'bot'}`;

    const now=new Date();
    const time=now.getHours().toString().padStart(2,'0')+':'+now.getMinutes().toString().padStart(2,'0');

    const safeSender=sanitize(sender);
    const safeText=isUser?sanitize(text):sanitizeWithLinks(text);

    container.innerHTML=`
      <div class="mv-message">
        <span class="mv-message-sender">${safeSender}</span>
        <div class="mv-message-text">${safeText}</div>
        <span class="mv-message-time">${time}</span>
      </div>
    `;

    messages.appendChild(container);
    messages.scrollTop=messages.scrollHeight;
  }

  const bot = widget.querySelector("#mv-bot");
  const chatbox = widget.querySelector("#mv-chatbox");
  const input = widget.querySelector("#mv-input");
  const messages = widget.querySelector("#mv-messages");
  const sendBtn = widget.querySelector("#mv-send");
  const headerBar = widget.querySelector("#mv-header-bar");

  const sessionKey="zeiSessionId";
  let sessionId=localStorage.getItem(sessionKey);
  if(!sessionId){
    sessionId="session_"+Date.now()+"_"+Math.random().toString(36).substring(2,8);
    localStorage.setItem(sessionKey,sessionId);
  }

  bot.addEventListener("click",()=>{
    const showing = chatbox.style.display !== "none";
    chatbox.style.display = showing ? "none" : "flex";

    if(!showing && !chatbox.dataset.greeted){
      appendMessage("Agente","¬°Hola! üëã Soy tu agente ISO27001 Mobilvendor. ¬øEn qu√© puedo ayudarte hoy?");
      chatbox.dataset.greeted=true;
    }
    if(!showing) input.focus();
  });

  input.addEventListener("focus",()=>headerBar.classList.add("active"));
  input.addEventListener("blur",()=>headerBar.classList.remove("active"));

  function sendMessage(){
    const msg=input.value.trim();
    if(!msg) return;

    appendMessage("T√∫",msg,true);

    input.value="";
    input.disabled=true;
    sendBtn.disabled=true;

    fetch(WEBHOOK_URL,{
      method:"POST",
      headers:{"Content-Type":"application/json"},
      body:JSON.stringify({sessionId,message:msg})
    })
    .then(r=>r.json())
    .then(data=>{
      const out=data.output || data.message || "Sin respuesta";
      appendMessage("Agente",out);
    })
    .catch(err=>{
      appendMessage("Agente","‚ùå Ocurri√≥ un error procesando tu mensaje.");
    })
    .finally(()=>{
      input.disabled=false;
      sendBtn.disabled=false;
      input.focus();
    });
  }

  input.addEventListener("keypress",e=>{ if(e.key==="Enter") sendMessage(); });
  sendBtn.addEventListener("click",sendMessage);

})();
