  // Segundo parseo: convierte string plano a objeto JSON
  const parsed = typeof unescaped === 'string' ? JSON.parse(unescaped) : unescaped;

  return [{ json: parsed }];
} catch (error) {
  throw new Error("âŒ Error al parsear JSON:\n" + error.message + "\n\nğŸ” Contenido:\n" + cleaned);
}

