const raw = $json["output"] || "";

// Elimina delimitadores y limpia caracteres extraÃ±os
let cleaned = raw
  .replace(/^```json\n?/, '')
  .replace(/```$/, '')
  .replace(/\u00A0/g, ' ')
  .trim();

// FunciÃ³n para corregir comas faltantes entre claves de objeto
const fixMissingCommas = (input) => {
  return input.replace(/}(\s*?)"(\w+)":/g, '},"$2":');
};

try {
  // Aplica correcciÃ³n de comas
  let fixedJson = fixMissingCommas(cleaned);

  // Primer parseo: convierte string JSON escapado a string plano
  let unescaped = JSON.parse(fixedJson);

  // Segundo parseo: convierte string plano a objeto JSON
  let parsed = typeof unescaped === 'string' ? JSON.parse(unescaped) : unescaped;

  return [{ json: parsed }];
} catch (error) {
  throw new Error("âŒ Error al parsear JSON:\n" + error.message + "\n\nğŸ” Contenido procesado:\n" + cleaned);
}

// FunciÃ³n para completar campos faltantes de forma recursiva
function completarCampos(obj, plantilla) {
  for (const key in plantilla) {
    if (typeof plantilla[key] === "object" && plantilla[key] !== null && !Array.isArray(plantilla[key])) {
      if (!obj[key]) obj[key] = {};
      completarCampos(obj[key], plantilla[key]);
    } else {
      if (obj[key] === undefined || obj[key] === null) {
        obj[key] = plantilla[key];
      }
    }
  }
  return obj;
}

