// Nodo Code: Formatear Fecha + Convertir Booleanos en Checkboxes

// --- 1) Formatear fecha en espaÃ±ol ---
const fechaOriginal = $json.general?.fecha || new Date().toISOString();
const fecha = new Date(fechaOriginal);

const meses = [
  "enero", "febrero", "marzo", "abril", "mayo", "junio",
  "julio", "agosto", "septiembre", "octubre", "noviembre", "diciembre"
];

const dia = fecha.getDate().toString().padStart(2, "0");
const mes = meses[fecha.getMonth()];
const anio = fecha.getFullYear();

// --- 2) Convertir booleanos a sÃ­mbolos de checkbox ---
function checkbox(value) {
  return value ? "â˜‘" : "â˜"; // puedes usar "âœ…" si prefieres un check verde
}

// Clonamos el objeto de entrada
const data = { ...$json };

// Sobrescribimos fecha
data.general = {
  ...data.general,
  fecha: `${dia} de ${mes} de ${anio}`
};

// Mapeamos alcanceGlobal
const alcanceGlobal = data.alcanceGlobal || {};
data.alcanceGlobal = {
  ...alcanceGlobal,
  moduloComercial: checkbox(alcanceGlobal.moduloComercial),
  facturacionElectronica: checkbox(alcanceGlobal.facturacionElectronica),
  moduloLogistico: checkbox(alcanceGlobal.moduloLogistico),
  moduloMerchandising: checkbox(alcanceGlobal.moduloMerchandising),
  desarrollosPersonalizados: checkbox(alcanceGlobal.desarrollosPersonalizados),
  integracion: checkbox(alcanceGlobal.integracion),
  moduloIntegracion: checkbox(alcanceGlobal.moduloIntegracion),
  integracionAPI: checkbox(alcanceGlobal.integracionAPI),
  integBasica: checkbox(alcanceGlobal.integBasica),
  integMedia: checkbox(alcanceGlobal.integMedia),
  integAvanzada: checkbox(alcanceGlobal.integAvanzada)
};

// --- 3) Retorno final ---
return { json: data };

const datosOriginales = $items("4. Formateo de datos")[0].json;  
const copiaDrive = $json; 

// âœ… ConstrucciÃ³n de requests con conversiÃ³n a String
function buildRequests(obj, prefix = "") {
  const result = [];
  for (const key in obj) {
    const value = obj[key];
    const fullKey = prefix ? `${prefix}.${key}` : key;

    if (typeof value === "object" && value !== null && !Array.isArray(value)) {
      result.push(...buildRequests(value, fullKey));
    } else if (fullKey.trim() !== "") {
      const safeValue = value === undefined || value === null ? "" : String(value);
      result.push({
        replaceAllText: {
          containsText: { text: `{{${fullKey}}}`, matchCase: true },
          replaceText: safeValue
        }
      });
    }
  }
  return result;
}

// âœ… Fecha: solo la generamos si no viene
if (!datosOriginales.general) datosOriginales.general = {};
if (!datosOriginales.general.fecha) {
  const fecha = new Date();
  const meses = ["enero","febrero","marzo","abril","mayo","junio","julio","agosto","septiembre","octubre","noviembre","diciembre"];
  const dia = String(fecha.getDate()).padStart(2,"0");
  datosOriginales.general.fecha = `${dia} de ${meses[fecha.getMonth()]} de ${fecha.getFullYear()}`;
}

// âœ… TransformaciÃ³n de booleanos en checkboxes
function checkbox(v){ return v ? "â˜‘" : "â˜"; }
function transformBooleans(obj){
  if (!obj) return;
  for (const k in obj){
    if (typeof obj[k] === "boolean") obj[k] = checkbox(obj[k]);
    else if (typeof obj[k] === "object") transformBooleans(obj[k]);
  }
}
transformBooleans(datosOriginales);

return {
  json: {
    ...datosOriginales,
    documentId: copiaDrive.id,
