    .replace(/Soluciones brindadas:/g, '</ul><p><b>Soluciones brindadas:</b></p><ul>')
    // Dividir por saltos de lÃ­nea, limpiar lÃ­neas vacÃ­as y eliminar "â€¢"
    .split(/\n+/)
    .filter(line => line.trim() !== '')
    .map(line => {
      if (!/<p><b>/.test(line)) {
