    }
  }
];

// Obtenemos el campo summary del JSON
const summaryObj = $json?.data?.transcript?.summary ?? null;

let summaryText = "vacio";

if (summaryObj && typeof summaryObj === "object") {
  // Si existe short_summary y no estÃ¡ vacÃ­o
  if (summaryObj.short_summary && summaryObj.short_summary.trim() !== "") {
    summaryText = "lleno";
  }
}

return [
  {
    json: {
      summary: summaryText,
      originalData: $json
    }
  }
];

