/*
  C√°lculo de cumplimiento semanal flexible (laboral)
  - Si viene semana manual ‚Üí calcula esa semana espec√≠fica (lunes‚Äìviernes)
  - Si NO ‚Üí semana laboral autom√°tica:
        * Lunes ‚Üí fin = viernes
        * Martes‚ÄìViernes ‚Üí fin = hoy
        * S√°bado/Domingo ‚Üí fin = viernes
  - Soporta fechas Fireflies: "Wed, Nov 12th - 08:05 AM-05:00 GMT (7 mins)"
*/

/* ============================================================
   CONFIGURACI√ìN MANUAL DE SEMANA (DENTRO DEL C√ìDIGO)
   ============================================================ */
const SEMANA_INICIO_MANUAL = ''; // <-- pon aqu√≠ el lunes que quieras, o deja '' para autom√°tico

const CONFIG = {
  "REUNI√ìN_D.ORGANIZACIONAL_DAILY": { grupo: "D. Organizacional", dias: ["LUNES","MARTES","MIERCOLES","JUEVES","VIERNES"], esperadas: 5 },
  "Daily - Soporte": { grupo: "Soporte", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 },
  "DAILY_COMERCIAL": { grupo: "Comercial", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 },
  "DAILY-TIC's": { grupo: "TICs", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 },
  "RESUMEN_DAILY_TICs": { grupo: "TICs", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 },
  "RESUMEN_DAILY_TCIS": { grupo: "TICs", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 },
  "RESUMEN_DAILY_TICS": { grupo: "TICs", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 },  
  "DAILY_MARKETING_TAREAS": { grupo: "Marketing", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 },
  "REUNION_FINANCIERO_LATAM_DAILY": { grupo: "Financiero LATAM", dias: ["LUNES"], esperadas: 1 },

  "REUNION_FINANCIERO_DAILY": { grupo: "Financiero", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 },
  // Aceptar ambas formas para Gerencia Weekly
  "REUNI√ìN_GERENCIA_WEEKLY": { grupo: "Gerencia", dias: ["LUNES"], esperadas: 1 },
  "Reuni√≥n_Gerencia_Weekly": { grupo: "Gerencia", dias: ["LUNES"], esperadas: 1 },

  // Todas estas pertenecen al mismo grupo ‚Üí esperadas deben ser 3 en total
  "REUNI√ìN_WEEKLY_OPERACIONES": { grupo: "Operaciones", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 },
  "REUNI√ìN_OPERACIONES_WEEKLY": { grupo: "Operaciones", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 },
  "REUNI√ìN_INHOUSE_WEEKLY": { grupo: "Operaciones", dias: ["LUNES","MIERCOLES","VIERNES"], esperadas: 3 }
};

function safeStr(v) {
  if (v === null || v === undefined) return "";
  return String(v);
}

/* ============================================================
      SELECCI√ìN DE SEMANA FLEXIBLE LABORAL
   ============================================================ */

function getMonday(date) {
  const d = new Date(date);
  const day = d.getDay();
  const diff = day === 0 ? -6 : 1 - day;
  d.setDate(d.getDate() + diff);
  d.setHours(0,0,0,0);
  return d;
}

let semanaManual = null;

// Semana manual desde c√≥digo
if (SEMANA_INICIO_MANUAL) {
  const d = new Date(SEMANA_INICIO_MANUAL);
  if (!isNaN(d)) semanaManual = d;
}

// Semana manual recibida desde JSON (opcional)
if (!semanaManual && $json.semanaInicio) {
  const d2 = new Date($json.semanaInicio);
  if (!isNaN(d2)) semanaManual = d2;
}

let inicio, fin;

if (semanaManual) {
  // Semana manual laboral ‚Üí lunes a viernes
  inicio = new Date(semanaManual);
  inicio.setHours(0,0,0,0);

  fin = new Date(inicio);
  fin.setDate(inicio.getDate() + 4); // viernes
  fin.setHours(23,59,59,999);

} else {
  // ==========================
  // semana laboral autom√°tica
  // ==========================
  const hoy = new Date();
  inicio = getMonday(hoy);

  const dia = hoy.getDay(); // 0 dom, 1 lun, ... 6 sab

  if (dia === 1) {
    // Lunes ‚Üí lunes a viernes
    fin = new Date(inicio);
    fin.setDate(inicio.getDate() + 4);
  } else if (dia >= 2 && dia <= 5) {
    // Martes‚ÄìViernes ‚Üí inicio lunes, fin hoy
    fin = new Date(hoy);
  } else {
    // S√°bado/Domingo ‚Üí cerrar en viernes
    fin = new Date(inicio);
    fin.setDate(inicio.getDate() + 4);
  }

  fin.setHours(23,59,59,999);
}

/* ============================================================
      PARSEO DE FECHAS FIRELIES / GOOGLESHEETS
   ============================================================ */

const MONTHS = {
  jan:0,feb:1,mar:2,apr:3,may:4,jun:5,
  jul:6,aug:7,sep:8,oct:9,nov:10,dec:11
};

function buildDateFromParts(year, monthIdx, dayNum, hour24, minute, offset) {
  const mm = String(monthIdx + 1).padStart(2,'0');
  const dd = String(dayNum).padStart(2,'0');
  const HH = String(hour24).padStart(2,'0');
  const MI = String(minute).padStart(2,'0');

  if (offset) {
    const iso = `${year}-${mm}-${dd}T${HH}:${MI}:00${offset}`;
    const d = new Date(iso);
    return isNaN(d) ? null : d;
  }
  const d = new Date(year, monthIdx, dayNum, hour24, minute);
  return isNaN(d) ? null : d;
}

function parseSpecialMeetingDate(raw) {
  const s = safeStr(raw).trim();
  if (!s) return null;

  const re = /^(?:Mon|Tue|Wed|Thu|Fri|Sat|Sun),?\s*([A-Za-z]{3})\s*(\d{1,2})(?:st|nd|rd|th)?\s*-\s*(\d{1,2}):(\d{2})\s*(AM|PM)(?:[^\d]*(\+\d{2}:\d{2}|-\d{2}:\d{2}))?/i;
  const m = s.match(re);
  if (!m) return null;

  const monthIdx = MONTHS[m[1].toLowerCase()];
  if (monthIdx === undefined) return null;

  let hh = Number(m[3]);
  const minute = Number(m[4]);
  const ampm = m[5].toUpperCase();
  const offset = m[6] || null;

  if (ampm === "PM" && hh !== 12) hh += 12;
  if (ampm === "AM" && hh === 12) hh = 0;

  const year = inicio.getFullYear();
  const dayNum = Number(m[2]);

  const candidates = [year, year - 1, year + 1];
  for (const y of candidates) {
    const d = buildDateFromParts(y, monthIdx, dayNum, hh, minute, offset);
    if (d && d >= inicio && d <= fin) return d;
  }

  return buildDateFromParts(year, monthIdx, dayNum, hh, minute, offset);
}

function parseSheetsDate(raw) {
  if (raw instanceof Date) return raw;

  if (typeof raw === "number") {
    const ms = Math.round((raw - 25569) * 86400 * 1000);
    const d = new Date(ms);
    return isNaN(d) ? null : d;
  }

  const special = parseSpecialMeetingDate(raw);
  if (special) return special;

  const s = safeStr(raw).trim();
  if (!s) return null;

  const d = new Date(s);
  if (!isNaN(d)) return d;

  const dm = s.match(/^(\d{1,2})\/(\d{1,2})\/(\d{4})$/);
  if (dm) {
    const dd = Number(dm[1]);
    const mm = Number(dm[2]) - 1;
    const yy = Number(dm[3]);
    const d2 = new Date(Date.UTC(yy, mm, dd));
    return isNaN(d2) ? null : d2;
  }

  return null;
}

/* ============================================================
      PROCESAMIENTO DE FILAS
   ============================================================ */

const stats = {};
for (const k of Object.keys(CONFIG)) {
  const c = CONFIG[k];
  if (!stats[c.grupo]) {
    stats[c.grupo] = {
      departamento: c.grupo,
      dias: c.dias,
      esperadas: c.esperadas,
      realizadas: 0,
      fechas: new Set(),
      textoChunks: []
    };
  }
}

const rows = $input.all();
for (const item of rows) {
  const r = item.json || {};
  const title = safeStr(r["Title"]).trim();
  if (!title || !CONFIG[title]) continue;

  const fecha = parseSheetsDate(r["Date"]);
  if (!fecha) continue;
  if (fecha < inicio || fecha > fin) continue;

  const cfg = CONFIG[title];
  const s = stats[cfg.grupo];

  // CORRECTO: contar cada reuni√≥n real (no por d√≠a)
  s.realizadas += 1;
  s.fechas.add(fecha.toISOString());

  const chunk = [
    safeStr(r["Gist"]),
    safeStr(r["Overview"]),
    safeStr(r["Notes"]),
    safeStr(r["Action Items"])
  ].join(" ").trim();

  if (chunk) s.textoChunks.push(chunk);
}

/* ============================================================
      ARMADO DEL RESULTADO
   ============================================================ */

const MAX_CHARS = 12000;

return Object.values(stats).map(s => {
  const texto = s.textoChunks.join("\n");
  const textoRecortado =
    texto.length > MAX_CHARS ? texto.slice(0, MAX_CHARS) + "\n... (recortado)" : texto;

  const cumplimiento =
    s.esperadas > 0 ? Math.round((s.realizadas / s.esperadas) * 100) : 0;

  return {
    json: {
      departamento: s.departamento,
      diasEsperados: s.dias,
      reunionesEsperadas: s.esperadas,
      reunionesRealizadas: s.realizadas,
      cumplimiento,
      texto: textoRecortado,
      semana: {
        inicio: inicio.toISOString().slice(0, 10),
        fin: fin.toISOString().slice(0, 10)
      }
    }
  };
});

