/*
  Extraer Resumen IA
  - Normaliza la salida del nodo OpenAI (langchain) a un campo: `resumen`
  - Soporta m√∫ltiples formatos de salida (choices, output, text, content, response, etc.)
  - Mantiene 1 item por item
*/

function safeStr(v) {
  if (v === null || v === undefined) return "";
  return String(v);
}

function getByPath(obj, path) {
  try {
    return path.split('.').reduce((acc, key) => {
      if (acc === null || acc === undefined) return undefined;
      const m = key.match(/^(\w+)\[(\d+)\]$/);
      if (m) {
        const k = m[1];
        const idx = Number(m[2]);
        return acc?.[k]?.[idx];
      }
      return acc?.[key];
    }, obj);
  } catch (e) {
    return undefined;
  }
}

const CANDIDATE_PATHS = [
  // OpenAI est√°ndar
  'choices[0].message.content',
  'choices[0].text',
  // Langchain/n8n variantes
  'output',
  'text',
  'content',
  'response',
  'resumen',
  'data.output',
  'data.text',
  'data.content',
  'data.response',
  'result.output',
  'result.text',
  'result.content',
  // Algunas estructuras de mensajes
  'message.content',
  'message',
  // Variantes con arrays
  'generations[0].text',
  'generations[0].message.content'
];

return $input.all().map(item => {
  const j = item.json || {};

  let resumen = "";
  for (const p of CANDIDATE_PATHS) {
    const v = getByPath(j, p);
    const s = safeStr(v).trim();
    if (s) {
      resumen = s;
      break;
    }
  }

  // Fallback: si existe cualquier string grande en el JSON, tomar el m√°s largo (solo si parece resumen)
  if (!resumen) {
    const strings = [];
    const stack = [j];
    const visited = new Set();
    while (stack.length) {
      const cur = stack.pop();
      if (!cur || typeof cur !== 'object') continue;
      if (visited.has(cur)) continue;
      visited.add(cur);
      for (const [k, v] of Object.entries(cur)) {
        if (typeof v === 'string') {
          const sv = v.trim();
          if (sv && sv.length >= 20) strings.push(sv);
        } else if (v && typeof v === 'object') {
          stack.push(v);
        }
      }
    }
    strings.sort((a, b) => b.length - a.length);
    if (strings[0]) resumen = strings[0];
  }

  const resumenFinal = resumen || "Sin novedades registradas.";

  return { json: { resumen: resumenFinal } };
});

/*
  Generar HTML de reporte
*/

const items = $input.all();
const semana = items?.[0]?.json?.semana || { inicio: "", fin: "" };

let html = `<h2>üìä Informe Semanal de Reuniones</h2>
<p><b>Semana:</b> ${semana.inicio} al ${semana.fin}</p>

<h3>1Ô∏è‚É£ Cumplimiento</h3>
<table border="1" cellpadding="6" style="border-collapse: collapse;">
<tr><th>Departamento</th><th>Cumplimiento</th><th>Realizadas</th><th>Esperadas</th></tr>`;

for (const i of items) {
  const j = i.json;
  const c = (j.cumplimiento >= 90) ? "green" : (j.cumplimiento >= 70) ? "orange" : "red";

  html += `<tr>
    <td>${j.departamento}</td>
    <td style="color:${c}"><b>${j.cumplimiento}%</b></td>
    <td>${j.reunionesRealizadas}</td>
    <td>${j.reunionesEsperadas}</td>
  </tr>`;
}

html += `</table>

<h3>2Ô∏è‚É£ Resumen por √°rea</h3>`;

for (const i of items) {
  const j = i.json;
  const resumen = (j.resumen || "Sin novedades registradas.").replace(/\n/g, "<br>");
  html += `<h4>${j.departamento}</h4><p>${resumen}</p>`;
}

return [{ json: { html } }];
